import { createGlobalStyle } from 'styled-components'


export const GlobalStyle = createGlobalStyle `

body {
    background: #1A1919;
    color: #FFF;
    font-family: 'Montserrat', sans-serif;

letter-spacing: -7px;
}

a {
    color: #FFF;
    text-decoration: none;
}



`
