import { createGlobalStyle } from 'styled-components'


export const GlobalStyle = createGlobalStyle `

body {
    background: #1A1919;
    color: #FFF;
    font-family: 'Montserrat', sans-serif;
}

a {
    color: #FFF;
    text-decoration: none;
}


letter-spacing: -7%;

`
