import { AnyAction } from '@reduxjs/toolkit'
import { all, put, takeEvery, call} from 'redux-saga/effects'

import { spotifyApi } from '../../api'


interface Itracks {
    album: {
        name:string;
        href:string;
        images: string[];
    }
}
interface IResponse {
    data: {
       tracks: Itracks[]
    }
}


function* getAlbuns(action: AnyAction){

    const { token } = action.payload;

    const config = {
        headers: {Authorization: token }
    }
    const response: IResponse = yield call(spotifyApi.get,
        '/recommendations?seed_genres=metal',
        config
        )

      const x = response.data.tracks.map((x:Itracks)=>{
          return {
              a:2
          }
      })  

    console.log(response.data)
    yield put({type: "spotify/GET_ALBUNS", payload: { albuns: response.data}})


}


export default function* spotifySaga(){
    yield all([
        takeEvery('@spotify/GET_ALBUNS', getAlbuns),

    ])
}
