import { createGlobalStyle } from 'styled-components'

interface IGlobalStyle {
    background:string
}
export const GlobalStyle = createGlobalStyle<IGlobalStyle> `

body {
    background: ${({background})=> background==="normal" ? '#1A1919' : 'linear-gradient(to bottom, #910614 0%, #910614 30%, rgba(0, 0, 0, 0.7));' };
    color: #FFF;
    font-family: 'Montserrat', sans-serif;
    letter-spacing: -7%;

    padding-top:60px;
    padding-left: 320px;

}

h1, h2, p, img, h3, h4 {
    padding:0;
    margin:0;
}
a {
    color: #FFF;
    text-decoration: none;
}

button {
    cursor:pointer;
}


`
