import { all, put, takeEvery, call} from 'redux-saga/effects'
import { push } from 'react-router-redux'

import { api }from '../../api'

function* signIn(){

    console.log('aaaaaaa')

    yield put(push())
    yield call(
        api.post, 
        '/auth/token'
    )
}



export default function* authSaga(){
    yield all([
        takeEvery('@auth/SIGN_IN', signIn)
    ])
}
