import { AnyAction } from '@reduxjs/toolkit'
import { all, put, takeEvery, call} from 'redux-saga/effects'

import { spotifyApi } from '../../api'


interface Iimages {
    url: string;
}
interface Itracks {
    album: {
        name:string;
        href:string;
        images: Iimages[];
        id: string;
        artists: [{
            name:string;
        }]
    }

    artists: [{
        id:string
    }]
}
interface IResponse {
    data: {
       tracks: Itracks[]
    }
}


 function* getAlbuns(action: AnyAction){

    const { token } = action.payload;

    const config = {
        headers: {Authorization: token }
    }
    const response: IResponse = yield call(spotifyApi.get,
        '/recommendations?seed_genres=metal',
        config
        )

      const albuns = response.data.tracks.map((x:Itracks)=>{
          return {
              name: x.album.name,
              images: x.album.images,
              id: x.album.id,
              link: x.album.href,
              artist: x.album.artists[0].name

          }
      })  

      console.log(response.data.tracks[0].artists[0].id)

      const artistsresponse: IResponse =  yield response.data.tracks.map( async (x:Itracks) => {

        const res: { data: string } =  await spotifyApi.get(
            `/artists/${x.artists[0].id}`,
            config)



        return res.data
      })


      console.log(artistsresponse)
    yield put({type: "spotify/GET_ALBUNS", payload: { albuns}})


}


export default function* spotifySaga(){
    yield all([
        takeEvery('@spotify/GET_ALBUNS', getAlbuns),

    ])
}
