import React, {useEffect, useState} from 'react'
import { AlbumContainer, Header, Content, PlayerDiv} from './styles'

import Cover from '../../images/Cover.png'
import { useLocation } from 'react-router-dom'
import PlayIcon from '../../images/PlayIcon.svg'

import { useSelector } from 'react-redux'

import { spotifyApi } from '../../api'
export default function Album(){


    const token = useSelector<string>((state:any)=>state?.auth?.token) 

    const array = [1, 2, 3, 4, 5, 6, 8, 9, 10]
    const [albumId, setAlbumId] = useState<string>('')
    const [album, setAlbum] = useState<string>('')
    const location = useLocation()

    useEffect(()=>{

        const [, , query] = location.pathname.split("/")

        setAlbumId(query)
        async function  getAlbum(){
            const config: any = {
                headers: { Authorization: token}
            }
           const response = await spotifyApi.get(`/albums/${query}`, config)
        }

        getAlbum()


        }, [location])

        // useEffect(()=>{

        // }, [locatio])
    return (
        <AlbumContainer>

            <Header>
            <img src={Cover} />
            <div>
            <h3> ÁLBUM </h3>
            <h1>Vulgar Display of Power</h1>
            <p> Pantera </p>
            </div>
            </Header>


            <Content>
                <PlayerDiv>
                    <img src={PlayIcon} />
                </PlayerDiv>
                <div className={'title'}>
                <h3> # Título </h3> 
                </div>

                {array.map(x=>(
                    <div className={'musics'}>
                    <h3>{x} Walk </h3>
                    <p> Pantera </p>
                    </div>
                ))}

            </Content>


        </AlbumContainer>
    )
}
