import styled from "styled-components";


export const HomeContainer = styled.div `

padding-top: 50px;

max-width:100%;

h2 {
    font-weight: 900;
}

p {
    font-weight:600;
}

div.panteraDiv {
    margin:20px 0px;
    width: 600px;
    height: 302px;
    background: #910614;
    border-radius: 4px;

    display:flex;
    align-items:center;
    padding:10px;

    h3 {
        font-weight:500;
        margin: 10px;
    }
    div.imageDiv {
        width:309px;
        height:290px;

        img {
            width:309px;
        height:290px; 
        }
    }
}
`
