import { all, put, takeEvery, call} from 'redux-saga/effects'
import { api }from '../../api'

function* signIn(){


    yield call(
        api.get, 
        '/auth/login'
    )
}



export default function* authSaga(){
    yield all([
        takeEvery('@auth/SIGN_IN', signIn)
    ])
}
