import styled from "styled-components";


export const AlbumContainer = styled.div`

margin-top:30px;
height:300px;

background: linear-gradient(to bottom, #910614 40%, #910614 30%, #212930);

`

export const Header = styled.div`
display:flex;
flex-wrap: wrap;
height:300px;
img {
    max-width:400px;
    height:300px;

}
div {
    height:140px;
    display:block;
    margin-top:auto;
    margin-bottom:0;
    margin-left: 10px;
}
h3 {
    font-size: 16px;


}

h1 {
    font-size:80px;
    font-weight:900;
}
`
