import styled from "styled-components";

interface IFlexContainer {
    isActived: boolean;
    width?: number;
}
export const SidebarContainer = styled.div`

background-color: #000;
max-width:40%;
width:300px;
height:100%;
padding-left: 10px;
padding-top: 10px;

position: fixed;
top:0;
left:0;

img.logo {
    width:240px;

    padding-bottom:40px;
}
`


export const FlexContainer = styled.div<IFlexContainer>`

display:flex;
justify-content:space-between;
max-width:200px;
align-items:center;

filter: ${({isActived})=> isActived? 'brightness(1.0)' : 'brightness(0.7)'};

&:hover {

}
img {
    width:40px;
}


p {

    display:block;
    margin-right: auto;
    padding-left: 10px;
    font-weight:600;
    font-size: 20px;
}
`
