import { createGlobalStyle } from 'styled-components'

interface IGlobalStyle {
    background:string
}
export const GlobalStyle = createGlobalStyle<IGlobalStyle> `

body {
    background: ${({background})=> background==="normal" ? '#1A1919' : 'linear-gradient(to bottom, #910614 20%, #910614 10%, rgba(0, 0, 0, 0.7), #000);' };



    color: #FFF;
    height: ${({background})=> background==="normal" ? '' : '2000px'};
    letter-spacing: -7%;

    padding-top:60px;
    padding-left: ${({background})=> background==="normal" ? '320px' : '295px'};

    padding-bottom:0px;
}

h1, h2, p, img, h3, h4{
    padding:0;
    margin:0;
}
a {
    color: #FFF;
    text-decoration: none;
}

button {
    cursor:pointer;
}


`
