import styled from "styled-components";


export const HeaderContainer = styled.div `

width:100%;
z-index:2;
position:fixed;
top:0;
left:0;

background:rgba(0, 0, 0, 0.7);

height:80px;

line-height:80px;


button {
    margin-left: auto;
}
`

export const OptionsDiv = styled.div `

background: #313131;
width:200px;
height:100px;

position: absolute;
right 0;
margin-right:10px;

font-size: 24px;

padding:3px 10px;

box-shadow: 1px 1px 1px 1px #212930;

border-radius:2px.

`
