import styled from "styled-components";


export const SidebarContainer = styled.div`

background-color: #000;
max-width:40%;
width:300px;
height:100%;
padding-left: 10px;
padding-top: 10px;

position: fixed;
top:0;
left:0;

img.logo {
    width:200px;

    padding-bottom:40px;
}
`


export const FlexContainer = styled.div`

display:flex;
justify-content:space-between;
max-width:100px;
align-items:center;

img {
    width:45px;
}


p {

    font-weight:600;
    font-size: 20px;
}
`
